import Axios from "axios";

const GET_DATA = 'app/coronaData/GET_DATA';

export function getData(values){
    return{type:GET_DATA , values }
}

export default function reducer(state={values:{}},action){
    switch(action.type){
        case GET_DATA:
            return {...state, values:action.values};
            default:
                return state;
    }
}


export function fetchData(){
    return (dispatch,getState)=>{

        Axios.get("https://covid19.mathdro.id/api").then((response)=>{

        const values = response.data;
        dispatch(getData(values))
        // console.log(response.data.confirmed.value)

        // const {confirmed,recovered,deaths} = response.data;
        // console.log(confirmed,recovered,deaths)

        }).catch((error)=>{

            console.log(error)

        })



    }
}
