import Axios from 'axios';

const FETCH_STATES ='app/countries/FETCH_STATES';

export function fetchStates(value){
    return{type:FETCH_STATES , value}
}


export default function reducer(state={states:[]}, action){
    switch(action.type){
            case FETCH_STATES:
                return {states:action.value}
            default:
                return state;
    }

}


export function getStates(value){
    return (dispatch, getState)=>{

        Axios.get(value).then((response)=>{

            const values = response.data.map((states)=>{

                return states;

            })

        dispatch(fetchStates(values))

        }).catch((error)=>{

            console.log(error)

        })

    }
}
