import React,{useState,useEffect} from 'react';

import {useDispatch} from 'react-redux';
import {useSelector} from 'react-redux';
import {fetchCountries} from '../ducks/countries';
import {Input, Select, Form} from 'antd';

function CountryPicker(){

    const[country,setCountry] = useState();
    const dispatch = useDispatch();


    useEffect(()=>{

        dispatch(fetchCountries());

    },[])

    const countries = useSelector((store)=>{

        return store.countries.countries;

    })

    console.log(countries)

    return( countries ? (

        <Input.Group >
      <Select style={{width:"30%"}} placeholder="Select a country">
        {countries.map((item,index)=>{

        return <Select.Option value={item.name} key={item.iso3}> {item.name} </Select.Option>

        })}
      </Select>
      </Input.Group>):null


    )
}

export default CountryPicker;
