import React,{useState,useEffect} from 'react';

import {useDispatch} from 'react-redux';
import {useSelector} from 'react-redux';
import {fetchCountries} from '../ducks/countries';
import {Input, Select, Form} from 'antd';

function CountryPicker(){

    const[country,setCountry] = useState();
    const dispatch = useDispatch();


    useEffect(()=>{

        dispatch(fetchCountries());

    },[])

    const countries = useSelector((store)=>{

        return store.countries.countries;

    })

    console.log(countries)

    return(
<>
        <Input.Group >
      <Select style={{width:"30%"}} placeholder="Select a country">
        {countries.map((item,index)=>{

        return <Select.Option value={item.name} key={item}> {item.name} </Select.Option>

        })}
      </Select>
      </Input.Group>

<List
size="small"
header={<div>Header</div>}
footer={<div>Footer</div>}
bordered
dataSource={countries}
renderItem={item => <List.Item>{item.name}</List.Item>}
/>
</>


    )
}

export default CountryPicker;
