import Axios from 'axios';
const COUNTRY_LIST = 'app/countries/COUNTRY_LIST';
const FETCH_COUNTRY ='app/countries/FETCH_COUNTRY';


export function getCountryList(value){
    return{type:COUNTRY_LIST, value}
}

export function fetchCountry(value){
    return{key:FETCH_COUNTRY , value}
}

export default function reducer(state={countries:[], country:[]}, action){
    switch(action.type){
        case COUNTRY_LIST:
            return {...state, countries:action.value}
            case FETCH_COUNTRY:
                return {...state, country:action.value}
            default:
                return state;
    }

}


export function fetchCountries(){
    return (dispatch,getState)=>{

        Axios.get('https://covid19.mathdro.id/api/countries').then((response)=>{

        const value = response.data.countries.map((country)=>{

            return country;

        })

        dispatch(getCountryList(value));

        }).catch((error)=>{

            console.log(error)

        })


    }
}

export function getCountry(value){
    return (dispatch, getState)=>{

        Axios.get('https://covid19.mathdro.id/api/countries/' + value).then((response)=>{

        dispatch(fetchCountry(response.data));

        }).catch((error)=>{

            console.log(error)

        })

    }
}
